基础定义trait 定义Parse trait并实现使用 use regex::Regex;
pub trait Parse { fn parse(s: &str) -> Self;
} impl Parse for u8 { fn parse(s: &str) -> Self { let re: Regex = Regex::new(r"^[0-9]+").unwrap(); if let Some(captures) = re.captures(s) { captures .get(0) .map_or(0, |s| s.as_str().parse().unwrap_or(0)) } else { 0 } }
} #[test]
fn parse_should_work() { assert_eq!(u8::parse("123abcd"), 123); assert_eq!(u8::parse("1234abcd"), 0); assert_eq!(u8::parse("abcd"), 0);
