pub trait Parse { fn parse(s: &str) -> Self;
} impl<T> Parse for T
where T: FromStr + Default,
{ fn parse(s: &str) -> Self { let re: Regex = Regex::new(r"^[0-9]+(\.[0-9]+)?").unwrap(); let d = || Default::default(); if let Some(captures) = re.captures(s) { captures .get(0) .map_or(d(), |s| s.as_str().parse().unwrap_or_else(|_| d())) } else { d() } }
} #[test]
fn parse_should_work() { assert_eq!(u32::parse("123abcd"), 123); assert_eq!(u32::parse("123.45abcd"), 0); assert_eq!(f64::parse("123.45abcd").to_string(), "123.45"); assert_eq!(f64::parse("abcd").to_string(), "0");
} fn main() { println!("result: {}", u8::parse("255 hello world"));
} 使用关联类型+添加Result<T, E> 关联类型自定义Error use std::str::FromStr; use regex::Regex;
pub trait Parse { type Error; fn parse(s: &str) -> Result<Self, Self::Error> where Self: Sized;
} impl<T> Parse for T
where T: FromStr + Default,
{ // 定义关联类型 Error 为 String type Error = String; fn parse(s: &str) -> Result<Self, Self::Error> { let re: Regex = Regex::new(r"^[0-9]+(\.[0-9]+)?").unwrap(); if let Some(captures) = re.captures(s) { // 当出错时我们返回 Err(String) captures .get(0) .map_or(Err("failed to capture".to_string()), |s| { s.as_str() .parse() .map_err(|_err| "failed to parse captured string".to_string()) }) } else { Err("failed to parse string".to_string()) } }
} #[test]
fn parse_should_work() { assert_eq!(u32::parse("123abcd"), Ok(123)); assert_eq!( u32::parse("123.45abcd"), Err("failed to parse captured string".into()) ); assert_eq!(f64::parse("123.45abcd"), Ok(123.45)); assert!(f64::parse("abcd").is_err());
} fn main() { println!("result: {:?}", u8::parse("255 hello world"));
}

