/// 缺省策略，从头到尾串行查找，最后输出到 writer
pub fn default_strategy( path: &Path, reader: &mut dyn BufRead, pattern: &Regex, writer: &mut dyn Write,
) -> Result<(), GrepError> { let matches: String = reader .lines() .enumerate() .map(|(lineno, line)| { line.ok() .map(|line| { pattern .find(&line) .map(|m| format_line(&line, lineno + 1, m.range())) }) .flatten() }) .filter_map(|v| v.ok_or(()).ok()) .join("\n"); if !matches.is_empty() { writer.write_all(path.display().to_string().green().as_bytes())?; writer.write_all(b"\n")?; writer.write_all(matches.as_bytes())?; writer.write_all(b"\n")?; } Ok(())
}

